CREATE TABLE `utilize_comoffs` ( `id` INT(16) NOT NULL AUTO_INCREMENT , `employee_id` INT(16) NOT NULL , `employee_name` VARCHAR(255) NOT NULL , `date_from` DATETIME NOT NULL , `date_to` DATETIME NOT NULL , `utilize_hours` FLOAT(10) NOT NULL DEFAULT '0' , `approved_by` VARCHAR(255) NOT NULL , `status` INT NOT NULL DEFAULT '0' , PRIMARY KEY (`id`), INDEX (`employee_id`)) ENGINE = InnoDB;